  }

  /////////////////////////// Utility functions

  $tw.boot.log = function (str) {
    $tw.boot.logMessages = $tw.boot.logMessages || []
    $tw.boot.logMessages.push(str)
  }

  /*
   * Check if an object has a property
   */
  $tw.utils.hop = function (object, property) {
    return object
      ? Object.prototype.hasOwnProperty.call(object, property)
      : false
  }

  /**
   * Determine if a value is an array
   */
  $tw.utils.isArray = function (value) {
    return Object.prototype.toString.call(value) === '[object Array]'
  }

  /**
   * Check if an array is equal by value and by reference.
   */
  $tw.utils.isArrayEqual = function (array1, array2) {
    if (array1 === array2) {
      return true
    }
    array1 = array1 || []
    array2 = array2 || []
    if (array1.length !== array2.length) {
      return false
    }
    return array1.every(function (value, index) {
      return value === array2[index]
    })
  }

  /**
   * Push entries onto an array, removing them first if they already exist in the array
   * array: array to modify (assumed to be free of duplicates)
   * value: a single value to push or an array of values to push
   */
  $tw.utils.pushTop = function (array, value) {
    var t, p
    if ($tw.utils.isArray(value)) {
      // Remove any array entries that are duplicated in the new values
      if (value.length !== 0) {
        if (array.length !== 0) {
          if (value.length < array.length) {
            for (t = 0; t < value.length; t++) {
              p = array.indexOf(value[t])
              if (p !== -1) {
                array.splice(p, 1)
              }
            }
          } else {
            for (t = array.length - 1; t >= 0; t--) {
              p = value.indexOf(array[t])
              if (p !== -1) {
                array.splice(t, 1)
              }
            }
          }
        }
        // Push the values on top of the main array
        array.push.apply(array, value)
      }
    } else {
      p = array.indexOf(value)
      if (p !== -1) {
        array.splice(p, 1)
      }
      array.push(value)
    }
    return array
  }

  /**
   * Determine if a value is a date
   */
  $tw.utils.isDate = function (value) {
    return Object.prototype.toString.call(value) === '[object Date]'
  }

  /**
   * Iterate through all the own properties of an object or array. Callback is invoked with (element,title,object)
   */
  $tw.utils.each = function (object, callback) {
    var next, f, length
    if (object) {
      if (Object.prototype.toString.call(object) === '[object Array]') {
        for (f = 0, length = object.length; f < length; f++) {
          next = callback(object[f], f, object)
          if (next === false) {
            break
          }
        }
      } else {
        var keys = Object.keys(object)
        for (f = 0, length = keys.length; f < length; f++) {
          var key = keys[f]
          next = callback(object[key], key, object)
          if (next === false) {
            break
          }
        }
      }
    }
  }

  /**
   * Helper for making DOM elements
   * tag: tag name
   * options: see below
   * Options include:
   * namespace: defaults to http://www.w3.org/1999/xhtml
   * attributes: hashmap of attribute values
   * style: hashmap of styles
   * text: text to add as a child node
   * children: array of further child nodes
   * innerHTML: optional HTML for element
   * class: class name(s)
   * document: defaults to current document
   * eventListeners: array of event listeners (this option won't work until $tw.utils.addEventListeners() has been loaded)
   */
  $tw.utils.domMaker = function (tag, options) {
    var doc = options.document || document
    var element = doc.createElementNS(
      options.namespace || 'http://www.w3.org/1999/xhtml',
      tag
    )
    if (options.class) {
      element.className = options.class
    }
    if (options.text) {
      element.appendChild(doc.createTextNode(options.text))
    }
    $tw.utils.each(options.children, function (child) {
      element.appendChild(child)
    })
    if (options.innerHTML) {
      element.innerHTML = options.innerHTML
    }
    $tw.utils.each(options.attributes, function (attribute, name) {
      element.setAttribute(name, attribute)
    })
    $tw.utils.each(options.style, function (value, name) {
      element.style[name] = value
    })
    if (options.eventListeners) {
      $tw.utils.addEventListeners(element, options.eventListeners)
    }
    return element
  }

  /**
   * Display an error and exit
   */
  $tw.utils.error = function (err) {
    // Prepare the error message
    var errHeading =
      $tw.language === undefined
        ? 'Internal JavaScript Error'
        : $tw.language.getString('InternalJavaScriptError/Title')
    var promptMsg =
      $tw.language === undefined
        ? 'Well, this is embarrassing. It is recommended that you restart TiddlyWiki by refreshing your browser'
        : $tw.language.getString('InternalJavaScriptError/Hint')
    // Log the error to the console
    console.error($tw.node ? '\x1b[1;31m' + err + '\x1b[0m' : err)
    if ($tw.browser && !$tw.node) {
      // Display an error message to the user
      var dm = $tw.utils.domMaker
      var heading = dm('h1', { text: errHeading })
      var prompt = dm('div', { text: promptMsg, class: 'tc-error-prompt' })
      var message = dm('div', { text: err, class: 'tc-error-message' })
      var button = dm('div', {
        children: [
          dm('button', {
            text:
              $tw.language === undefined
                ? 'close'
                : $tw.language.getString('Buttons/Close/Caption')
          })
        ],
        class: 'tc-error-prompt'
      })
      var form = dm('form', {
        children: [heading, prompt, message, button],
        class: 'tc-error-form'
      })
      document.body.insertBefore(form, document.body.firstChild)
      form.addEventListener(
        'submit',
        function (event) {
          document.body.removeChild(form)
          event.preventDefault()
          return false
        },
        true
      )
      return null
    } else if (!$tw.browser) {
      // Exit if we're under node.js
      process.exit(1)
    }
  }

  /**
   * Use our custom error handler if we're in the browser
   */
  if ($tw.boot.tasks.trapErrors) {
    window.onerror = function (errorMsg, url, lineNumber) {
      $tw.utils.error(errorMsg)
      return false
    }
  }

  /**
   * Extend an object with the properties from a list of source objects
   */
  $tw.utils.extend = function (object /*, sourceObjectList */) {
    $tw.utils.each(Array.prototype.slice.call(arguments, 1), function (source) {
      if (source) {
        for (var p in source) {
          object[p] = source[p]
        }
      }
    })
    return object
  }

  /**
   * Fill in any null or undefined properties of an object with the properties from a list of source objects.
   * Each property that is an object is called recursively
   */
  $tw.utils.deepDefaults = function (object /*, sourceObjectList */) {
    $tw.utils.each(Array.prototype.slice.call(arguments, 1), function (source) {
      if (source) {
        for (var p in source) {
          if (object[p] === null || object[p] === undefined) {
            object[p] = source[p]
          }
          if (typeof object[p] === 'object' && typeof source[p] === 'object') {
            $tw.utils.deepDefaults(object[p], source[p])
          }
        }
      }
    })
    return object
  }

  /**
   * Convert "&amp;" to &, "&nbsp;" to nbsp, "&lt;" to <, "&gt;" to > and "&quot;" to "
   */
  $tw.utils.htmlDecode = function (s) {
    return s
      .toString()
      .replace(/&lt;/gm, '<')
      .replace(/&nbsp;/gm, '\xA0')
      .replace(/&gt;/gm, '>')
      .replace(/&quot;/gm, '"')
      .replace(/&amp;/gm, '&')
  }

  /**
   * Get the browser location.hash. We don't use location.hash because of the way that Firefox auto-urldecodes it (see http://stackoverflow.com/questions/1703552/encoding-of-window-location-hash)
   */
  $tw.utils.getLocationHash = function () {
    var parts = window.location.href.split('#')
    return '#' + (parts.length > 1 ? parts[1] : '')
  }

  /**
   * Pad a string to a given length with "0"s. Length defaults to 2
   */
  $tw.utils.pad = function (value, length) {
    length = length || 2
    var s = value.toString()
    if (s.length < length) {
      s = '000000000000000000000000000'.substr(0, length - s.length) + s
    }
    return s
  }

  // Convert a date into UTC YYYYMMDDHHMMSSmmm format
  $tw.utils.stringifyDate = function (value) {
    return (
      value.getUTCFullYear() +
      $tw.utils.pad(value.getUTCMonth() + 1) +
      $tw.utils.pad(value.getUTCDate()) +
      $tw.utils.pad(value.getUTCHours()) +
      $tw.utils.pad(value.getUTCMinutes()) +
      $tw.utils.pad(value.getUTCSeconds()) +
      $tw.utils.pad(value.getUTCMilliseconds(), 3)
    )
  }

  // Parse a date from a UTC YYYYMMDDHHMMSSmmm format string
  $tw.utils.parseDate = function (value) {
    if (typeof value === 'string') {
      return new Date(
        Date.UTC(
          parseInt(value.substr(0, 4), 10),
          parseInt(value.substr(4, 2), 10) - 1,
          parseInt(value.substr(6, 2), 10),
          parseInt(value.substr(8, 2) || '00', 10),
          parseInt(value.substr(10, 2) || '00', 10),
          parseInt(value.substr(12, 2) || '00', 10),
          parseInt(value.substr(14, 3) || '000', 10)
        )
      )
    } else if ($tw.utils.isDate(value)) {
      return value
    } else {
      return null
    }
  }

  // Stringify an array of tiddler titles into a list string
  $tw.utils.stringifyList = function (value) {
    if ($tw.utils.isArray(value)) {
      var result = new Array(value.length)
      for (var t = 0, l = value.length; t < l; t++) {
        var entry = value[t] || ''
        if (entry.indexOf(' ') !== -1) {
          result[t] = '[[' + entry + ']]'
        } else {
          result[t] = entry
        }
      }
      return result.join(' ')
    } else {
      return value || ''
    }
  }

  // Parse a string array from a bracketted list. For example "OneTiddler [[Another Tiddler]] LastOne"
  $tw.utils.parseStringArray = function (value, allowDuplicate) {
    if (typeof value === 'string') {
      var memberRegExp = /(?:^|[^\S\xA0])(?:\[\[(.*?)\]\])(?=[^\S\xA0]|$)|([\S\xA0]+)/gm
      var results = []
      var names = {}
      var match
      do {
        match = memberRegExp.exec(value)
        if (match) {
          var item = match[1] || match[2]
          if (
            item !== undefined &&
            (!$tw.utils.hop(names, item) || allowDuplicate)
          ) {
            results.push(item)
            names[item] = true
          }
        }
      } while (match)
      return results
    } else if ($tw.utils.isArray(value)) {
      return value
    } else {
      return null
    }
  }

  // Parse a block of name:value fields. The `fields` object is used as the basis for the return value
  $tw.utils.parseFields = function (text, fields) {
    fields = fields || Object.create(null)
    text.split(/\r?\n/gm).forEach(function (line) {
      if (line.charAt(0) !== '#') {
        var p = line.indexOf(':')
        if (p !== -1) {
          var field = line.substr(0, p).trim()
          var value = line.substr(p + 1).trim()
          if (field) {
            fields[field] = value
          }
        }
      }
    })
    return fields
  }

  /**
   * Resolves a source filepath delimited with `/` relative to a specified absolute root filepath.
   * In relative paths, the special folder name `..` refers to immediate parent directory, and the
   * name `.` refers to the current directory
   */
  $tw.utils.resolvePath = function (sourcepath, rootpath) {
    // If the source path starts with ./ or ../ then it is relative to the root
    if (sourcepath.substr(0, 2) === './' || sourcepath.substr(0, 3) === '../') {
      var src = sourcepath.split('/')
      var root = rootpath.split('/')
      // Remove the filename part of the root
      root.splice(root.length - 1, 1)
      // Process the source path bit by bit onto the end of the root path
      while (src.length > 0) {
        var c = src.shift()
        if (c === '..') {
          // Slice off the last root entry for a double dot
          if (root.length > 0) {
            root.splice(root.length - 1, 1)
          }
        } else if (c !== '.') {
          // Ignore dots
          root.push(c) // Copy other elements across
        }
      }
      return root.join('/')
    } else {
      // If it isn't relative, just return the path
      if (rootpath) {
        var root = rootpath.split('/')
        // Remove the filename part of the root
        root.splice(root.length - 1, 1)
        return root.join('/') + '/' + sourcepath
      } else {
        return sourcepath
      }
    }
  }

  /**
   * Parse a semantic version string into its constituent parts -- see https://semver.org
   */
  $tw.utils.parseVersion = function (version) {
    /*eslint no-useless-escape:"off"*/
    var match = /^v?((\d+)\.(\d+)\.(\d+))(?:-([\dA-Za-z\-]+(?:\.[\dA-Za-z\-]+)*))?(?:\+([\dA-Za-z\-]+(?:\.[\dA-Za-z\-]+)*))?$/.exec(
      version
    )
    if (match) {
      return {
        version: match[1],
        major: parseInt(match[2], 10),
        minor: parseInt(match[3], 10),
        patch: parseInt(match[4], 10),
        prerelease: match[5],
        build: match[6]
      }
    } else {
      return null
    }
  }

  /**
   * Returns +1 if the version string A is greater than the version string B, 0 if they are the same, and +1 if B is greater than A.
   * Missing or malformed version strings are parsed as 0.0.0
   */
  $tw.utils.compareVersions = function (versionStringA, versionStringB) {
    var defaultVersion = {
      major: 0,
      minor: 0,
      patch: 0
    }
    var versionA = $tw.utils.parseVersion(versionStringA) || defaultVersion
    var versionB = $tw.utils.parseVersion(versionStringB) || defaultVersion
    var diff = [
      versionA.major - versionB.major,
      versionA.minor - versionB.minor,
      versionA.patch - versionB.patch
    ]
    if (
      diff[0] > 0 ||
      (diff[0] === 0 && diff[1] > 0) ||
      (diff[0] === 0) & (diff[1] === 0) & (diff[2] > 0)
    ) {
      return +1
    } else if (
      diff[0] < 0 ||
      (diff[0] === 0 && diff[1] < 0) ||
      (diff[0] === 0) & (diff[1] === 0) & (diff[2] < 0)
    ) {
      return -1
    } else {
      return 0
    }
  }

  /**
   * Returns true if the version string A is greater than the version string B. Returns true if the versions are the same
   */
  $tw.utils.checkVersions = function (versionStringA, versionStringB) {
    return $tw.utils.compareVersions(versionStringA, versionStringB) !== -1
  }

  /**
   * Register file type information
   * options: {flags: flags,deserializerType: deserializerType}
   * flags:"image" for image types
   * deserializerType: defaults to type if not specified
   */
  $tw.utils.registerFileType = function (type, encoding, extension, options) {
    options = options || {}
    if ($tw.utils.isArray(extension)) {
      $tw.utils.each(extension, function (extension) {
        $tw.config.fileExtensionInfo[extension] = { type: type }
      })
      extension = extension[0]
    } else {
      $tw.config.fileExtensionInfo[extension] = { type: type }
    }
    $tw.config.contentTypeInfo[type] = {
      encoding: encoding,
      extension: extension,
      flags: options.flags || [],
      deserializerType: options.deserializerType || type
    }
  }

  /**
   * Given an extension, always access the $tw.config.fileExtensionInfo
   * using a lowercase extension only.
   */
  $tw.utils.getFileExtensionInfo = function (ext) {
    return ext ? $tw.config.fileExtensionInfo[ext.toLowerCase()] : null
  }

  /**
   * Given an extension, get the correct encoding for that file.
   * defaults to utf8
   */
  $tw.utils.getTypeEncoding = function (ext) {
    var extensionInfo = $tw.utils.getFileExtensionInfo(ext)
    var type = extensionInfo ? extensionInfo.type : null
    var typeInfo = type ? $tw.config.contentTypeInfo[type] : null
    return typeInfo ? typeInfo.encoding : 'utf8'
  }

  /**
   * Run code globally with specified context variables in scope
   */
  $tw.utils.evalGlobal = function (code, context, filename) {
    var contextCopy = $tw.utils.extend(Object.create(null), context)
    // Get the context variables as a pair of arrays of names and values
    var contextNames = []
    var contextValues = []
    $tw.utils.each(contextCopy, function (value, name) {
      contextNames.push(name)
      contextValues.push(value)
    })
    // Add the code prologue and epilogue
    code =
      '(function(' +
      contextNames.join(',') +
      ') {(function(){\n' +
      code +
      '\n;})();\nreturn exports;\n})\n'
    // Compile the code into a function
    var fn
    if ($tw.browser) {
      /*eslint no-eval:"off"*/
      fn = window.eval(code + '\n\n//# sourceURL=' + filename)
    } else {
      fn = vm.runInThisContext(code, filename)
    }
    // Call the function and return the exports
    return fn.apply(null, contextValues)
  }

  /*
   * Run code in a sandbox with only the specified context variables in scope
   */
  $tw.utils.evalSandboxed = $tw.browser
    ? $tw.utils.evalGlobal
    : function (code, context, filename) {
        var sandbox = $tw.utils.extend(Object.create(null), context)
        vm.runInNewContext(code, sandbox, filename)
        return sandbox.exports
      }

  /**
   * Creates a PasswordPrompt object
   */
  $tw.utils.PasswordPrompt = function () {
    // Store of pending password prompts
    this.passwordPrompts = []
    // Create the wrapper
    this.promptWrapper = $tw.utils.domMaker('div', {
      class: 'tc-password-wrapper'
    })
    document.body.appendChild(this.promptWrapper)
    // Hide the empty wrapper
    this.setWrapperDisplay()
  }

  /**
   * Hides or shows the wrapper depending on whether there are any outstanding prompts
   */
  $tw.utils.PasswordPrompt.prototype.setWrapperDisplay = function () {
    if (this.passwordPrompts.length) {
      this.promptWrapper.style.display = 'block'
    } else {
      this.promptWrapper.style.display = 'none'
    }
  }

  /**
   * Adds a new password prompt. Options are:
   * submitText: text to use for submit button (defaults to "Login")
   * serviceName: text of the human readable service name
   * noUserName: set true to disable username prompt
   * canCancel: set true to enable a cancel button (callback called with null)
   * repeatPassword: set true to prompt for the password twice
   * callback: function to be called on submission with parameter of object {username:,password:}.
   *  Callback must return `true` to remove the password prompt
   */
  $tw.utils.PasswordPrompt.prototype.createPrompt = function (options) {
    // Create and add the prompt to the DOM
    var self = this
    var submitText = options.submitText || 'Login'
    var dm = $tw.utils.domMaker
    var children = [dm('h1', { text: options.serviceName })]
    if (!options.noUserName) {
      children.push(
        dm('input', {
          attributes: {
            type: 'text',
            name: 'username',
            placeholder: $tw.language.getString('Encryption/Username')
          }
        })
      )
    }
    children.push(
      dm('input', {
        attributes: {
          type: 'password',
          name: 'password',
          placeholder:
            $tw.language === undefined
              ? 'Password'
              : $tw.language.getString('Encryption/Password')
        }
      })
    )
    if (options.repeatPassword) {
      children.push(
        dm('input', {
          attributes: {
            type: 'password',
            name: 'password2',
            placeholder: $tw.language.getString('Encryption/RepeatPassword')
          }
        })
      )
    }
    if (options.canCancel) {
      children.push(
        dm('button', {
          text: $tw.language.getString('Encryption/Cancel'),
          attributes: {
            type: 'button'
          },
          eventListeners: [
            {
              name: 'click',
              handlerFunction: function (event) {
                self.removePrompt(promptInfo)
                options.callback(null)
              }
            }
          ]
        })
      )
    }
    children.push(
      dm('button', {
        attributes: { type: 'submit' },
        text: submitText
      })
    )
    var form = dm('form', {
      attributes: { autocomplete: 'off' },
      children: children
    })
    this.promptWrapper.appendChild(form)
    window.setTimeout(function () {
      form.elements[0].focus()
    }, 10)
    // Add a submit event handler
    var self = this
    form.addEventListener(
      'submit',
      function (event) {
        // Collect the form data
        var data = {}
        $tw.utils.each(form.elements, function (element) {
          if (element.name && element.value) {
            data[element.name] = element.value
          }
        })
        // Check that the passwords match
        if (options.repeatPassword && data.password !== data.password2) {
          alert($tw.language.getString('Encryption/PasswordNoMatch'))
        } else {
          // Call the callback
          if (options.callback(data)) {
            // Remove the prompt if the callback returned true
            self.removePrompt(promptInfo)
          } else {
            // Clear the password if the callback returned false
            $tw.utils.each(form.elements, function (element) {
              if (element.name === 'password' || element.name === 'password2') {
                element.value = ''
              }
            })
          }
        }
        event.preventDefault()
        return false
      },
      true
    )
    // Add the prompt to the list
    var promptInfo = {
      serviceName: options.serviceName,
      callback: options.callback,
      form: form
    }
    this.passwordPrompts.push(promptInfo)
    // Make sure the wrapper is displayed
    this.setWrapperDisplay()
  }

  $tw.utils.PasswordPrompt.prototype.removePrompt = function (promptInfo) {
    var i = this.passwordPrompts.indexOf(promptInfo)
    if (i !== -1) {
      this.passwordPrompts.splice(i, 1)
      promptInfo.form.parentNode.removeChild(promptInfo.form)
      this.setWrapperDisplay()
    }
  }

  /**
   * Crypto helper object for encrypted content. It maintains the password text in a closure, and provides methods to change
   * the password, and to encrypt/decrypt a block of text
   */
  $tw.utils.Crypto = function () {
    var sjcl = $tw.node ? global.sjcl || require('./sjcl.js') : window.sjcl
    var currentPassword = null
    var callSjcl = function (method, inputText, password) {
      password = password || currentPassword
      var outputText
      try {
        if (password) {
          outputText = sjcl[method](password, inputText)
        }
      } catch (ex) {
        console.log('Crypto error:' + ex)
        outputText = null
      }
      return outputText
    }
    this.setPassword = function (newPassword) {
      currentPassword = newPassword
      this.updateCryptoStateTiddler()
    }
    this.updateCryptoStateTiddler = function () {
      if ($tw.wiki) {
        var state = currentPassword ? 'yes' : 'no'
        var tiddler = $tw.wiki.getTiddler('$:/isEncrypted')
        if (!tiddler || tiddler.fields.text !== state) {
          $tw.wiki.addTiddler(
            new $tw.Tiddler({ title: '$:/isEncrypted', text: state })
          )
        }
      }
    }
    this.hasPassword = function () {
      return !!currentPassword
    }
    this.encrypt = function (text, password) {
      return callSjcl('encrypt', text, password)
    }
    this.decrypt = function (text, password) {
      return callSjcl('decrypt', text, password)
    }
  }

  /////////////////////////// Module mechanism

  /**
   * Execute the module named 'moduleName'. The name can optionally be relative to the module named 'moduleRoot'
   */
  $tw.modules.execute = function (moduleName, moduleRoot) {
    var name = moduleName
    if (moduleName.charAt(0) === '.') {
      name = $tw.utils.resolvePath(moduleName, moduleRoot)
    }
    if (!$tw.modules.titles[name]) {
      if ($tw.modules.titles[name + '.js']) {
        name = name + '.js'
